  //        });
  //      }
  //      this.setState({
  //        inputcode1: newState
  //      });
  //    });
  //  }

  render() {
  return (
    < div className='app'>
    < header>
      < div className="wrapper">
        < h1> Codes < /h1>
            < / div>
              < /header>
                < div className='container'>
                  < section className='add-item'>
                    < input type="text" name="currentItem" placeholder="Enter Code" onChange={ this.handleChange } value={ this.state.currentItem } />
                    < Button onClick={()=>{this.setState({show:!this.state.show})}}> Enter Code < /Button>
                        < / section>
                          < section className='display-item'>
                            < div className="wrapper">
                              < ul>
                              <div>
                              {
                              this.state.show? <Button>Roll</Button> : null
                              }

                              </div>
                               < /ul>
                                      < / div>
                                        < /section>
                                          < / div>
                                            < /div>
                                              );
                                              }
                                              }
export default App;
