import React, { Component } from 'react';
import './App.css'
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css'

class App extends Component {
  constructor() {
    super();
    this.state = {
      currentItem: '',
      username: ''
    }
  }
  render() {
    return (
      <div className='app'>
      <header>
        <div className='wrapper'>
        <h1>Codes for the SlothMachine</h1>

        </div>

      </header>
      <div className='container'>
      <section className="add-item">
        <form>
          <input type="text" name="username" placeholder="navn?" onChange={this.handleChange} value={this.state.username} />
          <input type="text" name="currentItem" placeholder="code?" onChange={this.handleChange} value={this.state.currentItem} />
          <Button>Add Item</Button>
        </form>
      </section>
      <section className='display-item'>
            <div className='wrapper'>
            <ul>
            </ul>
          </div>
          </section>
          </div>
          </div>


    );
  }
}

export default App
