import React, { Component } from 'react';
import './App.css'
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css'
import firebase from './firebase.js'

class App extends Component {
  constructor() {
    super();
    this.state = {
      currentItem: [],
      SlothCode: '',
      inputcode: []
    }

    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(e) {
    e.preventDefault(); //removes default behaviors like reloading on pressing submit.
    const inputcodeRef = firebase.database().ref('inputcode');
    const item = {
      SlothCode: this.state.currentItem
    }
    inputcodeRef.push(item);
    this.setState({
      currentItem: '',
      SlothCode: ''
    });
  }
  componentDidMount() {
    const inputcodeRef = firebase.database().ref('inputcode');
    inputcodeRef.on('value', (snapshot) => {
      let inputcode = snapshot.val();
      let newState = [];
      for (let item in inputcode) {
        newState.push({
          id: item,
          SlothCode: inputcode[item].SlothCode
        });
      }
      this.setState({
        inputcode: newState
      });
    });
  }

  render() {
    return (
      <div className='app'>
        <header>
            <div className="wrapper">
              <h1>Codes</h1>
            </div>
        </header>
        <div className='container'>
          <section className='add-item'>

                  <input type="text" name="currentItem" placeholder="Enter Code" onChange={this.handleChange} value={this.state.currentItem} />
                <Button onClick={this.handleSubmit}>Enter Code</Button>
          </section>
          <section className='display-item'>
              <div className="wrapper">
                <ul>
                  {this.state.inputcode.map((item) => {
                    return (
                      <li>
                        <h3>{item.SlothCode}</h3>
                      </li>
                    )
                  })}
                </ul>
              </div>
          </section>
        </div>
      </div>
    );
  }
}
export default App;
