import React, {
  Component
} from 'react';
import './App.css'
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css'
import firebase from './firebase.js'

class App extends Component {
  constructor() {
    super();
    this.state = {
      show:false,
      currentItem: [],

      inputcode1: []
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(e) {
    this.setState({
      [e.target.name]: e.target.value
    });
  }




  handleSubmit(e) {

    const codesRef = firebase.database().ref();
    const inputcode1Ref = firebase.database().ref("inputcode1");
    inputcode1Ref.set({
      SlothCode: e.target.value
    }).then(function() {
      codesRef.once("value")
        .then(function(snapshot) {
          if (snapshot.child("inputcode1/SlothCode").val() === snapshot.child("inputcode2/SlothCode1").val()) {
            console.log("same!");
            return(<Button/>);
            firebase.database().ref("Payment").push({Paid:true});
          } else {
            console.log("does not exist.")
          }
        })
    });
  }
  renderComp(){
     if(this.state.loginSuccess===true){
        return(<Button/>);
      }
      else{
          return(<div>Incorrect UserName or Password</div>)
      }

  }


  render() {
  return (
    < div className='app'>
    < header>
      < div className="wrapper">
        < h1> Codes < /h1>
            < / div>
              < /header>
                < div className='container'>
                  < section className='add-item'>
                    < input placeholder="Indtast kode her" type="text" name="currentItem"  onChange={ this.handleChange } value={ this.state.currentItem } />

                        < / section>
                        < Button onClick={ this.handleSubmit } value={ this.state.currentItem }> Enter Code < /Button>
                          < section className='display-item'>
                            < div className="wrapper">
                              < ul>
                              <div>
                              {
                              this.state.show? <div>{this.renderComp}</div> : null
                              }

                              </div>
                               < /ul>
                                      < / div>
                                        < /section>
                                          < / div>
                                            < /div>
                                              );
                                              }
                                              }
export default App;
